(1, TIMESTAMP '2025-04-29 14:56:32.926626', U&'<!DOCTYPE html>\000a<html lang="en">\000a<head>\000a    <meta charset="UTF-8">\000a    <meta name="viewport" content="width=device-width, initial-scale=1.0">\000a    <title>Nebula Data Management Platform</title>\000a    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.23.5/antd.min.css">\000a    <style>\000a        body {\000a            font-family: "Arial", "sans-serif";\000a            margin: 20px;\000a            color: #333;\000a        }\000a        .container {\000a            max-width: 1200px;\000a            margin: 0 auto;\000a        }\000a        .header {\000a            display: flex;\000a            justify-content: space-between;\000a            align-items: center;\000a            margin-bottom: 20px;\000a        }\000a        .header h1 {\000a            margin: 0;\000a            color: #65AC1E;\000a        }\000a        .ant-btn-primary {\000a            background-color: #65AC1E;\000a            border-color: #65AC1E;\000a        }\000a        .ant-btn-primary:hover {\000a            background-color: #56981A;\000a            border-color: #56981A;\000a        }\000a        .ant-modal-header {\000a            background-color: #65AC1E;\000a            color: white;\000a        }\000a        .ant-table-thead > tr > th {\000a            position: relative;\000a        }\000a        .filter-icon {\000a            position: absolute;\000a            right: 10px;\000a            top: 50%;\000a            transform: translateY(-50%);\000a            cursor: pointer;\000a        }\000a    </style>\000a</head>\000a<body>\000a    <div class="container">\000a        <div class="header">\000a            <h1>Nebula Data Management Platform</h1>\000a            <button class="ant-btn ant-btn-primary" onclick="showAddModal()">Add Nebula</button>\000a        </div>\000a        <table class="ant-table">\000a            <thead class="ant-table-thead">\000a                <tr>\000a                    <th>ID <span class="filter-icon" onclick="showFilter(''id'')">\+01f50d</span></th>\000a                    <th>Name <span class="filter-icon" onclick="showFilter(''name'')">\+01f50d</span></th>\000a                    <th>Location <span class="filter-icon" onclick="showFilter(''location'')">\+01f50d</span></th>\000a                    <th>Brightness <span class="filter-icon" onclick="showFilter(''brightness'')">\+01f50d</span></th>\000a                    <th>Stars Count <span class="filter-icon" onclick="showFilter(''starCount'')">\+01f50d</span></th>\000a                    <th>Created Time <span class="filter-icon" onclick="showFilter(''createdTime'')">\+01f50d</span></th>\000a                    <th>Creator <span class="filter-icon" onclick="showFilter(''creator'')">\+01f50d</span></th>\000a                    <th>Modified Time <span class="filter-icon" onclick="showFilter(''modifiedTime'')">\+01f50d</span></th>\000a                    <th>Modifier <span class="filter-icon" onclick="showFilter(''modifier'')">\+01f50d</span></th>\000a                    <th>Remarks <span class="filter-icon" onclick="showFilter(''remarks'')">\+01f50d</span></th>\000a                    <th>Actions</th>\000a                </tr>\000a            </thead>\000a            <tbody id="nebula-list" class="ant-table-tbody">\000a                <!-- Nebula rows will be dynamically generated -->\000a            </tbody>\000a        </table>\000a        <div class="ant-pagination" style="margin-top: 20px; text-align: right;">\000a            <span id="pagination-info"></span>\000a            <div style="display: inline-flex; align-items: center;">\000a                <button class="ant-btn" onclick="prevPage()">Previous</button>\000a                <button class="ant-btn" onclick="nextPage()">Next</button>\000a            </div>\000a        </div>\000a    </div>\000a\000a    <!-- Filter Modal -->\000a    <div id="filter-modal" class="ant-modal" style="display: none;">\000a        <div class="ant-modal-content">\000a            <div class="ant-modal-header">\000a                <div class="ant-modal-title">Filter</div>\000a            </div>\000a            <div class="ant-modal-body">\000a                <input type="text" id="filter-input" class="ant-input" placeholder="Enter filter value">\000a            </div>\000a            <div class="ant-modal-footer">\000a                <button class="ant-btn" onclick="closeFilter()">Cancel</button>\000a                <button class="ant-btn ant-btn-primary" onclick="applyFilter()">Apply</button>\000a            </div>\000a        </div>\000a    </div>\000a\000a    <!-- Add Modal -->\000a    <div id="add-modal" class="ant-modal" style="display: none;">\000a        <div class="ant-modal-content">\000a            <div class="ant-modal-header">\000a                <div class="ant-modal-title">Add Nebula</div>\000a            </div>\000a            <div class="ant-modal-body">\000a                <input type="text" id="add-name" class="ant-input" placeholder="Name">\000a                <input type="text" id="add-location" class="ant-input" placeholder="Location" style="margin-top: 10px;">\000a                <input type="number" id="add-brightness" class="ant-input" placeholder="Brightness" style="margin-top: 10px;">\000a                <input type="number" id="add-starCount" class="ant-input" placeholder="Stars Count" style="margin-top: 10px;">\000a                <input type="text" id="add-remarks" class="ant-input" placeholder="Remarks" style="margin-top: 10px;">\000a            </div>\000a            <div class="ant-modal-footer">\000a                <button class="ant-btn" onclick="closeAddModal()">Cancel</button>\000a                <button class="ant-btn ant-btn-primary" onclick="addNebula()">Add</button>\000a            </div>\000a        </div>\000a    </div>\000a\000a    <!-- Edit Modal -->\000a    <div id="edit-modal" class="ant-modal" style="display: none;">\000a        <div class="ant-modal-content">\000a            <div class="ant-modal-header">\000a                <div class="ant-modal-title">Edit Nebula</div>\000a            </div>\000a            <div class="ant-modal-body">\000a                <input type="text" id="edit-name" class="ant-input" placeholder="Name">\000a                <input type="text" id="edit-location" class="ant-input" placeholder="Location" style="margin-top: 10px;">\000a                <input type="number" id="edit-brightness" class="ant-input" placeholder="Brightness" style="margin-top: 10px;">\000a                <input type="number" id="edit-starCount" class="ant-input" placeholder="Stars Count" style="margin-top: 10px;">\000a                <input type="text" id="edit-remarks" class="ant-input" placeholder="Remarks" style="margin-top: 10px;">\000a            </div>\000a            <div class="ant-modal-footer">\000a                <button class="ant-btn" onclick="closeEditModal()">Cancel</button>\000a                <button class="ant-btn ant-btn-primary" onclick="saveEditNebula()">Save</button>\000a            </div>\000a        </div>\000a    </div>\000a\000a    <script>\000a        let nebulaData = [];\000a        let currentPage = 1;\000a        const pageSize = 10;\000a        const totalNebulaCount = 25;\000a        let filterColumn = '''';\000a        let filterValue = '''';\000a\000a        function generateData() {\000a            for (let i = 1; i <= totalNebulaCount; i++) {\000a                nebulaData.push({\000a                    id: i,\000a                    name: `Nebula ${i}`,\000a                    location: `Location ${i}`,\000a                    brightness: (Math.random() * 100).toFixed(2),\000a                    starCount: Math.floor(Math.random() * 1000),\000a                    createdTime: `2023-10-${String(i).padStart(2, ''0'')}`,\000a                    creator: `Creator ${i}`,\000a                    modifiedTime: `2023-11-${String(i).padStart(2, ''0'')}`,\000a                    modifier: `Modifier ${i}`,\000a                    remarks: `Remark ${i}`\000a                });\000a            }\000a        }\000a\000a        function renderTable() {\000a            const filteredData = nebulaData.filter(item => {\000a                if (!filterValue) return true;\000a                return String(item[filterColumn]).toLowerCase().includes(filterValue.toLowerCase());\000a            });\000a\000a            const startIndex = (currentPage - 1) * pageSize;\000a            const pageData = filteredData.slice(startIndex, startIndex + pageSize);\000a\000a            const tbody = document.getElementById(''nebula-list'');\000a            tbody.innerHTML = '''';\000a\000a            pageData.forEach(nebula => {\000a                const row = document.createElement(''tr'');\000a                row.innerHTML = `\000a                    <td>${nebula.id}</td>\000a                    <td>${nebula.name}</td>\000a                    <td>${nebula.location}</td>\000a                    <td>${nebula.brightness}</td>\000a                    <td>${nebula.starCount}</td>\000a                    <td>${nebula.createdTime}</td>\000a                    <td>${nebula.creator}</td>\000a                    <td>${nebula.modifiedTime}</td>\000a                    <td>${nebula.modifier}</td>\000a                    <td>${nebula.remarks}</td>\000a                    <td>\000a                        <button class="ant-btn ant-btn-link" onclick="showEditModal(${nebula.id})">Edit</button>\000a                        <button class="ant-btn ant-btn-link ant-btn-danger" onclick="deleteNebula(${nebula.id})">Delete</button>\000a                    </td>\000a                `;\000a                tbody.appendChild(row);\000a            });\000a\000a            document.getElementById(''pagination-info'').textContent = `Page: ${currentPage} / ${Math.ceil(filteredData.length / pageSize)} Total: ${filteredData.length}`;\000a        }\000a\000a        function prevPage() {\000a            if (currentPage > 1) {\000a                currentPage--;\000a                renderTable();\000a            }\000a        }\000a\000a        function nextPage() {\000a            const filteredData = nebulaData.filter(item => {\000a                if (!filterValue) return true;\000a                return String(item[filterColumn]).toLowerCase().includes(filterValue.toLowerCase());\000a            });\000a            if (currentPage < Math.ceil(filteredData.length / pageSize)) {\000a                currentPage++;\000a                renderTable();\000a            }\000a        }\000a\000a        function showFilter(column) {\000a            filterColumn = column;\000a            document.getElementById(''filter-modal'').style.display = ''block'';\000a        }\000a\000a        function closeFilter() {\000a            document.getElementById(''filter-modal'').style.display = ''none'';\000a        }\000a\000a        function applyFilter() {\000a            filterValue = document.getElementById(''filter-input'').value;\000a            currentPage = 1;\000a            renderTable();\000a            closeFilter();\000a        }\000a\000a        function showAddModal() {\000a            document.getElementById(''add-name'').value = '''';\000a            document.getElementById(''add-location'').value = '''';\000a            document.getElementById(''add-brightness'').value = '''';\000a            document.getElementById(''add-starCount'').value = '''';\000a            document.getElementById(''add-remarks'').value = '''';\000a            document.getElementById(''add-modal'').style.display = ''block'';\000a        }\000a\000a        function closeAddModal() {\000a            document.getElementById(''add-modal'').style.display = ''none'';\000a        }\000a\000a        function addNebula() {\000a            const name = document.getElementById(''add-name'').value;\000a            const location = document.getElementById(''add-location'').value;\000a            const brightness = document.getElementById(''add-brightness'').value;\000a            const starCount = document.getElementById(''add-starCount'').value;\000a            const remarks = document.getElementById(''add-remarks'').value;\000a\000a            const newNebula = {\000a                id: nebulaData.length + 1,\000a                name,\000a                location,\000a                brightness,\000a                starCount,\000a                createdTime: new Date().toISOString().split(''T'')[0],\000a                creator: ''Admin'',\000a                modifiedTime: new Date().toISOString().split(''T'')[0],\000a                modifier: ''Admin'',\000a                remarks\000a            };\000a\000a            nebulaData.push(newNebula);\000a            renderTable();\000a            closeAddModal();\000a        }\000a\000a        function showEditModal(id) {\000a            const nebula = nebulaData.find(item => item.id === id);\000a            document.getElementById(''edit-name'').value = nebula.name;\000a            document.getElementById(''edit-location'').value = nebula.location;\000a            document.getElementById(''edit-brightness'').value = nebula.brightness;\000a            document.getElementById(''edit-starCount'').value = nebula.starCount;\000a            document.getElementById(''edit-remarks'').value = nebula.remarks;\000a\000a            document.getElementById(''edit-modal'').dataset.id = id;\000a            document.getElementById(''edit-modal'').style.display = ''block'';\000a        }\000a\000a        function closeEditModal() {\000a            document.getElementById(''edit-modal'').style.display = ''none'';\000a        }\000a\000a        function saveEditNebula() {\000a            const id = parseInt(document.getElementById(''edit-modal'').dataset.id);\000a            const nebula = nebulaData.find(item => item.id === id);\000a\000a            nebula.name = document.getElementById(''edit-name'').value;\000a            nebula.location = document.getElementById(''edit-location'').value;\000a            nebula.brightness = document.getElementById(''edit-brightness'').value;\000a            nebula.starCount = document.getElementById(''edit-starCount'').value;\000a            nebula.remarks = document.getElementById(''edit-remarks'').value;\000a            nebula.modifiedTime = new Date().toISOString().split(''T'')[0];\000a            nebula.modifier = ''Admin'';\000a\000a            renderTable();\000a            closeEditModal();\000a        }\000a\000a        function deleteNebula(id) {\000a            if (confirm(`Are you sure you want to delete Nebula ${id}?`)) {\000a                nebulaData = nebulaData.filter(nebula => nebula.id !== id);\000a                renderTable();\000a            }\000a        }\000a\000a        generateData();\000a        renderTable();\000a    </script>\000a</body>\000a</html>', 'e4ad63fd-efb4-422b-9e6f-4f40a839c838');     
