-sizing: border-box;\000a        }\000a        table th:hover .filter-box {\000a            display: block;\000a        }\000a        .add-button {\000a            float: right;\000a            margin-bottom: 16px;\000a            padding: 8px 16px;\000a            background-color: #4CAF50;\000a            color: white;\000a            border: none;\000a            cursor: pointer;\000a            border-radius: 4px;\000a        }\000a        .add-button:hover {\000a            background-color: #45a049;\000a        }\000a        .action-buttons button {\000a            padding: 4px 8px;\000a            border: none;\000a            cursor: pointer;\000a            border-radius: 4px;\000a            font-size: 14px;\000a            background-color: #4CAF50;\000a            color: white;\000a        }\000a        .action-buttons button:hover {\000a            background-color: #45a049;\000a        }\000a        .pagination {\000a            display: flex;\000a            justify-content: space-between;\000a            align-items: center;\000a            margin: 16px 0;\000a        }\000a        .pagination .info {\000a            font-size: 14px;\000a        }\000a        .pagination .controls {\000a            display: flex;\000a            gap: 8px;\000a        }\000a        .pagination button {\000a            padding: 6px 12px;\000a            border: none;\000a            background-color: #4CAF50;\000a            color: white;\000a            cursor: pointer;\000a            border-radius: 4px;\000a        }\000a        .pagination button:disabled {\000a            background-color: #BEBEBE;\000a            cursor: not-allowed;\000a        }\000a        .modal {\000a            display: none;\000a            position: fixed;\000a            top: 50%;\000a            left: 50%;\000a            transform: translate(-50%, -50%);\000a            background: white;\000a            padding: 20px;\000a            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);\000a            z-index: 1000;\000a            border-radius: 8px;\000a        }\000a        .modal.active {\000a            display: block;\000a        }\000a        .modal-header {\000a            font-size: 20px;\000a            margin-bottom: 16px;\000a        }\000a        .modal-body input {\000a            display: block;\000a            width: 100%;\000a            margin-bottom: 12px;\000a            padding: 8px;\000a            box-sizing: border-box;\000a        }\000a        .modal-footer {\000a            display: flex;\000a            justify-content: flex-end;\000a            gap: 8px;\000a        }\000a        .modal-footer button {\000a            padding: 8px 16px;\000a            border: none;\000a            border-radius: 4px;\000a            cursor: pointer;\000a            font-size: 14px;\000a        }\000a        .modal-footer .save {\000a            background-color: #4CAF50;\000a            color: white;\000a        }\000a        .modal-footer .cancel {\000a            background-color: #FF4500;\000a            color: white;\000a        }\000a        .overlay {\000a            display: none;\000a            position: fixed;\000a            top: 0;\000a            left: 0;\000a            width: 100%;\000a            height: 100%;\000a            background: rgba(0, 0, 0, 0.5);\000a            z-index: 999;\000a        }\000a        .overlay.active {\000a            display: block;\000a        }\000a    </style>\000a</head>\000a<body>\000a    <header>Stellar Data Management Platform</header>\000a    <div class="container">\000a        <button class="add-button" id="add-button">Add</button>\000a        <table>\000a            <thead>\000a                <tr>\000a                    <th>Name <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-name" placeholder="Filter Name">\000a                        </div>\000a                    </th>\000a                    <th>Location <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-location" placeholder="Filter Location">\000a                        </div>\000a                    </th>\000a                    <th>Brightness <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-brightness" placeholder="Filter Brightness">\000a                        </div>\000a                    </th>\000a                    <th>Star Count <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-star-count" placeholder="Filter Star Count">\000a                        </div>\000a                    </th>\000a                    <th>Created Time <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-created-time" placeholder="Filter Created Time">\000a                        </div>\000a                    </th>\000a                    <th>Created By <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-created-by" placeholder="Filter Created By">\000a                        </div>\000a                    </th>\000a                    <th>Modified Time <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-modified-time" placeholder="Filter Modified Time">\000a                        </div>\000a                    </th>\000a                    <th>Modified By <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-modified-by" placeholder="Filter Modified By">\000a                        </div>\000a                    </th>\000a                    <th>Remark <button class="filter">\25bc</button>\000a                        <div class="filter-box">\000a                            <input type="text" id="filter-remark" placeholder="Filter Remark">\000a                        </div>\000a                    </th>\000a                    <th>Actions</th>\000a                </tr>\000a            </thead>\000a            <tbody>\000a                <!-- Placeholder for dynamically generated rows -->\000a            </tbody>\000a        </table>\000a        <div class="pagination">\000a            <div class="info">\000a                Total <span id="total-records">25</span> records, Page <span id="current-page">1</span> of <span id="total-pages">3</span>\000a            </div>\000a            <div class="controls">\000a                <button id="prev-page" disabled>Previous</button>\000a                <button id="next-page">Next</button>\000a            </div>\000a        </div>\000a    </div>\000a\000a    <div class="modal" id="modal">\000a        <div class="modal-header" id="modal-title">Add Nebula</div>\000a        <div class="modal-body">\000a            <input type="text" id="modal-name" placeholder="Name">\000a            <input type="text" id="modal-location" placeholder="Location">\000a            <input type="number" id="modal-brightness" placeholder="Brightness">\000a            <input type="number" id="modal-star-count" placeholder="Star Count">\000a            <input type="text" id="modal-created-time" placeholder="Created Time">\000a            <input type="text" id="modal-created-by" placeholder="Created By">\000a            <input type="text" id="modal-modified-time" placeholder="Modified Time">\000a            <input type="text" id="modal-modified-by" placeholder="Modified By">\000a            <input type="text" id="modal-remark" placeholder="Remark">\000a        </div>\000a        <div class="modal-footer">\000a            <button class="save" id="modal-save">Save</button>\000a            <button class="cancel" id="modal-cancel">Cancel</button>\000a        </div>\000a    </div>\000a    <div class="overlay" id="overlay"></div>\000a\000a    <script>\000a        const nebulaeData = Array.from({ length: 25 }, (_, i) => ({\000a            name: `Nebula ${i + 1}`,\000a            location: `Location ${i + 1}`,\000a            brightness: Math.round(Math.random() * 100),\000a            starCount: Math.round(Math.random() * 1000),\000a            createdTime: `2023-01-${Math.ceil(Math.random() * 30)} 12:00`,\000a            createdBy: `User ${Math.ceil(Math.random() * 5)}`,\000a            modifiedTime: `2023-02-${Math.ceil(Math.random() * 28)} 12:00`,\000a            modifiedBy: `User ${Math.ceil(Math.random() * 5)}`,\000a            remark: `Remark ${i + 1}`\000a        }));\000a\000a        const rowsPerPage = 10;\000a        let currentPage = 1;\000a        const totalPages = Math.ceil(nebulaeData.length / rowsPerPage);\000a\000a        const modal = document.getElementById(''modal'');\000a        const overlay = document.getElementById(''overlay'');\000a        const modalTitle = document.getElementById(''modal-title'');\000a        const modalName = document.getElementById(''modal-name'');\000a        const modalLocation = document.getElementById(''modal-location'');\000a        const modalBrightness = document.getElementById(''modal-brightness'');\000a        const modalStarCount = document.getElementById(''modal-star-count'');\000a        const modalCreatedTime = document.getElementById(''modal-created-time'');\000a        const modalCreatedBy = document.getElementById(''modal-created-by'');\000a        const modalModifiedTime = document.getElementById(''modal-modified-time'');\000a        const modalModifiedBy = document.getElementById(''modal-modified-by'');\000a        const modalRemark = document.getElementById(''modal-remark'');\000a        let editIndex = null;\000a\000a        const filters = {\000a            name: '''',\000a            location: '''',\000a            brightness: '''',\000a            starCount: '''',\000a            createdTime: '''',\000a            createdBy: '''',\000a            modifiedTime: '''',\000a            modifiedBy: '''',\000a            remark: ''''\000a        };\000a\000a        const openModal = (title, record = {}) => {\000a            modalTitle.textContent = title;\000a            modalName.value = record.name || '''';\000a            modalLocation.value = record.location || '''';\000a            modalBrightness.value = record.brightness || '''';\000a            modalStarCount.value = record.starCount || '''';\000a            modalCreatedTime.value = record.createdTime || '''';\000a            modalCreatedBy.value = record.createdBy || '''';\000a            modalModifiedTime.value = record.modifiedTime || '''';\000a            modalModifiedBy.value = record.modifiedBy || '''';\000a            modalRemark.value = record.remark || '''';\000a            modal.classList.add(''active'');\000a            overlay.classList.add(''active'');\000a        };\000a\000a        const closeModal = () => {\000a            modal.classList.remove(''active'');\000a            overlay.classList.remove(''active'');\000a        };\000a\000a        const applyFilters = () => {\000a            return nebulaeData.filter(record => {\000a                return Object.keys(filters).every(key => {\000a                    return filters[key] === '''' || record[key].toString().toLowerCase().includes(filters[key].toLowerCase());\000a                });\000a            });\000a        };\000a\000a        const renderTable = () => {\000a            const tbody = document.querySelector(''tbody'');\000a            tbody.innerHTML = '''';\000a            const filteredData = applyFilters();\000a            const start = (currentPage - 1) * rowsPerPage;\000a            const end = start + rowsPerPage;\000a            const currentData = filteredData.slice(start, end);\000a\000a            currentData.forEach((record, index) => {\000a                const row = document.createElement(''tr'');\000a                for (const key in record) {\000a                    const cell = document.createElement(''td'');\000a                    cell.textContent = record[key];\000a                    row.appendChild(cell);\000a                }\000a                const actionCell = document.createElement(''td'');\000a                actionCell.className = ''action-buttons'';\000a                const editButton = document.createElement(''button'');\000a                editButton.textContent = ''Edit'';\000a                editButton.addEventListener(''click'', () => {\000a                    editIndex = filteredData.indexOf(record);\000a                    openModal(''Edit Nebula'', record);\000a                });\000a                const deleteButton = document.createElement(''button'');\000a                deleteButton.textContent = ''Delete'';\000a                deleteButton.addEventListener(''click'', () => {\000a                    if (confirm(''Are you sure you want to delete '' + record.name + ''?'')) {\000a                        nebulaeData.splice(filteredData.indexOf(record), 1);\000a                        renderTable();\000a                    }\000a                });\000a                actionCell.appendChild(editButton);\000a                actionCell.appendChild(deleteButton);\000a                row.appendChild(actionCell);\000a                tbody.appendChild(row);\000a            });\000a\000a            document.getElementById(''total-records'').textContent = filteredData.length;\000a            document.getElementById(''current-page'').textContent = currentPage;\000a            document.getElementById(''total-pages'').textContent = Math.ceil(filteredData.length / rowsPerPage);\000a            document.getElementById(''prev-page'').disabled = currentPage === 1;\000a            document.getElementById(''next-page'').disabled = currentPage === Math.ceil(filteredData.length / rowsPerPage);\000a        };\000a\000a        document.querySelectorAll(''.filter-box input'').forEach(input => {\000a            input.addEventListener(''input'', event => {\000a                const id = event.target.id.replace(''filter-'', '''');\000a                filters[id] = event.target.value;\000a                currentPage = 1;\000a                renderTable();\000a            });\000a        });\000a\000a        document.getElementById(''prev-page'').addEventListener(''click'', () => {\000a            if (currentPage > 1) {\000a                currentPage--;\000a                renderTable();\000a            }\000a        });\000a\000a        document.getElementById(''next-page'').addEventListener(''click'', () => {\000a            if (currentPage < totalPages) {\000a                currentPage++;\000a                renderTable();\000a            }\000a        });\000a\000a        document.getElementById(''add-button'').addEventListener(''click'', () => {\000a            editIndex = null;\000a            openModal(''Add Nebula'');\000a        });\000a\000a        document.getElementById(''modal-save'').addEventListener(''click'', () => {\000a            const newRecord = {\000a                name: modalName.value,\000a                location: modalLocation.value,\000a                brightness: modalBrightness.value,\000a                starCount: modalStarCount.value,\000a                createdTime: modalCreatedTime.value,\000a                createdBy: modalCreatedBy.value,\000a                modifiedTime: modalModifiedTime.value,\000a                modifiedBy: modalModifiedBy.value,\000a                remark: modalRemark.value\000a            };\000a\000a            if (editIndex !== null) {\000a                nebulaeData[editIndex] = newRecord;\000a            } else {\000a                nebulaeData.push(newRecord);\000a            }\000a\000a            closeModal();\000a            renderTable();\000a        });\000a\000a        document.getElementById(''modal-cancel'').addEventListener(''click'', () => {\000a            closeModal();\000a        });\000a\000a        overlay.addEventListener(''click'', () => {\000a            closeModal();\000a        });\000a\000a        renderTable();\000a    </script>\000a</body>\000a</html>', '7d2e1c5f-7b25-4a3b-b4f5-39273d53e363');   
