(66, TIMESTAMP '2025-04-29 15:43:41.279735', U&'<!DOCTYPE html>\000a<html lang="zh-CN">\000a<head>\000a    <meta charset="UTF-8">\000a    <meta name="viewport" content="width=device-width, initial-scale=1.0">\000a    <title>\65bd\8010\5fb7\5de5\4e1a\77e5\8bc6\56fe\8c31\539f\578b</title>\000a    <style>\000a        body {\000a            margin: 0;\000a            font-family: "PingFang SC", "Microsoft YaHei", "Noto Sans CJK SC", "Arial", sans-serif;\000a            -webkit-font-smoothing: antialiased;\000a            background-color: #F5F5F5;\000a        }\000a        .graph-container {\000a            width: 100vw;\000a            height: 100vh;\000a            position: relative;\000a            background-color: #FFFFFF;\000a            display: flex;\000a            flex-direction: column;\000a        }\000a        .map-container {\000a            flex: 2;\000a            background-image: url(''https://img2.baidu.com/it/u=4114447086,133264338&fm=253&fmt=auto&app=138&f=JPEG?w=750&h=500'');\000a            background-size: cover;\000a            background-position: center;\000a            position: relative;\000a        }\000a        .map-tooltip {\000a            position: absolute;\000a            background: rgba(255, 255, 255, 0.9);\000a            border: 1px solid #E3E3E3;\000a            padding: 8px;\000a            font-size: 14px;\000a            line-height: 22px;\000a            pointer-events: none;\000a            box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\000a        }\000a        .dashboard-container {\000a            flex: 1;\000a            background-color: #F0F0F0;\000a            display: flex;\000a            justify-content: space-around;\000a            align-items: center;\000a            padding: 20px;\000a        }\000a        .chart, .gauge {\000a            width: 30%;\000a            height: 100%;\000a            background: #FFFFFF;\000a            border: 1px solid #E3E3E3;\000a            border-radius: 10px;\000a            box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\000a            display: flex;\000a            justify-content: center;\000a            align-items: center;\000a        }\000a        .data-display {\000a            position: absolute;\000a            padding: 6px;\000a            background: rgba(255, 255, 255, 0.9);\000a            border: 1px solid #E3E3E3;\000a            border-radius: 4px;\000a            font-size: 12px;\000a            pointer-events: none;\000a            box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\000a            text-align: center;\000a        }\000a        .button {\000a            position: fixed;\000a            bottom: 20px;\000a            right: 20px;\000a            padding: 12px 20px;\000a            background-color: #007BFF;\000a            color: #FFFFFF;\000a            border: none;\000a            border-radius: 4px;\000a            cursor: pointer;\000a            font-size: 14px;\000a            line-height: 22px;\000a        }\000a        .button:hover {\000a            background-color: #0056b3;\000a        }\000a    </style>\000a    <script src="https://cdnjs.cloudflare.com/ajax/libs/echarts/5.4.2/echarts.min.js"></script>\000a</head>\000a<body>\000a    <div class="graph-container">\000a        <div class="map-container" id="factory-map">\000a            <div class="data-display" id="sensor1-display" style="left: 20%; top: 30%;"></div>\000a            <div class="data-display" id="sensor2-display" style="left: 50%; top: 50%;"></div>\000a            <div class="data-display" id="sensor3-display" style="left: 70%; top: 20%;"></div>\000a        </div>\000a        <div class="dashboard-container">\000a            <div class="gauge" id="temperature-gauge"></div>\000a            <div class="chart" id="pressure-chart"></div>\000a            <div class="chart" id="energy-chart"></div>\000a        </div>\000a        <button class="button" onclick="resetGraph()">\91cd\7f6e\89c6\56fe</button>\000a    </div>\000a\000a    <script>\000a        let sensorData = [\000a            { id: ''sensor1'', name: ''\4f20\611f\56681'', value: 50, status: ''\6b63\5e38'' },\000a            { id: ''sensor2'', name: ''\4f20\611f\56682'', value: 75, status: ''\5f02\5e38: \538b\529b\8fc7\9ad8'' },\000a            { id: ''sensor3'', name: ''\4f20\611f\56683'', value: 30, status: ''\6b63\5e38'' }\000a        ];\000a\000a        function initializeMap() {\000a            sensorData.forEach(sensor => {\000a                const display = document.getElementById(`${sensor.id}-display`);\000a                display.innerHTML = `${sensor.name}<br>\503c: ${sensor.value}<br>${sensor.status}`;\000a            });\000a        }\000a\000a        function updateSensorData() {\000a            sensorData.forEach(sensor => {\000a                sensor.value = (Math.random() * 100).toFixed(1);\000a                sensor.status = sensor.value > 70 ? ''\5f02\5e38: \503c\8fc7\9ad8'' : ''\6b63\5e38'';\000a                const display = document.getElementById(`${sensor.id}-display`);\000a                display.innerHTML = `${sensor.name}<br>\503c: ${sensor.value}<br>${sensor.status}`;\000a                display.style.backgroundColor = sensor.status.includes(''\5f02\5e38'') ? ''#FFCCCC'' : ''#CCFFCC'';\000a            });\000a        }\000a\000a        function initializeDashboard() {\000a            const temperatureGauge = echarts.init(document.getElementById(''temperature-gauge''));\000a            const pressureChart = echarts.init(document.getElementById(''pressure-chart''));\000a            const energyChart = echarts.init(document.getElementById(''energy-chart''));\000a\000a            const gaugeOption = {\000a                series: [{\000a                    type: ''gauge'',\000a                    detail: { formatter: ''{value}\00b0C'' },\000a                    data: [{ value: 20, name: ''\6e29\5ea6'' }]\000a                }]\000a            };\000a\000a            const pressureOption = {\000a                xAxis: {\000a                    type: ''category'',\000a                    data: Array.from({ length: 31 }, (_, i) => `Step ${i}`)\000a                },\000a                yAxis: {\000a                    type: ''value'',\000a                    name: ''\538b\529b (MPa)''\000a                },\000a                series: [{\000a                    data: Array.from({ length: 31 }, (_, i) => (i * 100 / 30).toFixed(1)),\000a                    type: ''line'',\000a                    name: ''\538b\529b\76d1\6d4b''\000a                }]\000a            };\000a\000a            const energyOption = {\000a                xAxis: {\000a                    type: ''category'',\000a                    data: [''1\6708'', ''2\6708'', ''3\6708'', ''4\6708'']\000a                },\000a                yAxis: {\000a                    type: ''value'',\000a                    name: ''\80fd\8017 (kWh)''\000a                },\000a                series: [{\000a                    data: [1200, 1500, 1100, 1300],\000a                    type: ''bar'',\000a                    name: ''\80fd\8017\5206\6790''\000a                }]\000a            };\000a\000a            temperatureGauge.setOption(gaugeOption);\000a            pressureChart.setOption(pressureOption);\000a            energyChart.setOption(energyOption);\000a\000a            let startTime = Date.now();\000a\000a            function updateDashboard() {\000a                const elapsedTime = (Date.now() - startTime) / 600000; // In 10-minute cycles\000a                const temperature = 45 + 25 * Math.sin(2 * Math.PI * elapsedTime); // Sine wave between 20 and 70\000a                gaugeOption.series[0].data[0].value = temperature.toFixed(1);\000a                temperatureGauge.setOption(gaugeOption);\000a\000a                const pressureElapsed = ((Date.now() - startTime) / 30000) % 1; // 30-second cycles\000a                pressureOption.series[0].data = Array.from({ length: 31 }, (_, i) => ((i + pressureElapsed * 30) % 30) * 100 / 30);\000a                pressureChart.setOption(pressureOption);\000a\000a                energyOption.series[0].data = energyOption.series[0].data.map(() => Math.floor(Math.random() * 2000));\000a                energyChart.setOption(energyOption);\000a            }\000a\000a            setInterval(updateDashboard, 1000);\000a\000a            window.addEventListener("resize", () => {\000a                temperatureGauge.resize();\000a                pressureChart.resize();\000a                energyChart.resize();\000a            });\000a        }\000a\000a        function resetGraph() {\000a            initializeMap();\000a            initializeDashboard();\000a        }\000a\000a        initializeMap();\000a        initializeDashboard();\000a        setInterval(updateSensorData, 1000);\000a    </script>\000a</body>\000a</html>', '923b5438-379b-491d-8255-b2b420541200');     
